(function(win){if(typeof win.CXFun!='undefined'){alert('函数名：CXFun被占用，请联系张中乐解决！');return}var CXFun=function(){};CXFun.prototype.zperiod=function(n){var zperiodDom=$("#zperiod");var zinitArr=['第一节','第二节','第三节','第四节','第五节','第六节','第七节','第八节','第九节','第十节','第十一节','第十二节','第十三节','第十四节','第十五节','第十六节'];function getCount(len){var arr=[];for(var i=0;i<len;i+=1){arr.push(zinitArr[i])}return arr}function getPeriod(start,len){var arr=[];for(var i=start;i<zperiodArr.length;i+=1){arr.push({id:i+'',value:zperiodArr[i]})}return arr}var zhi=function(zhi){var arr=[];for(var i=0;i<zhi;i+=1){arr.push({id:i,value:'至'})}return arr};var zperiodArr=getCount(n);var startData=function(callback){callback(getPeriod(0))};var zhiData=function(date,callback){callback(zhi(1))};var endData=function(date,hour,callback){callback(getPeriod(date))};zperiodDom.bind('click',function(){var oneLevelId=zperiodDom.find('span').attr('data-start');var twoLevelId=zperiodDom.find('span').attr('data-zhi');var threeLevelId=zperiodDom.find('span').attr('data-end');var iosSelect=new IosSelect(3,[startData,zhiData,endData],{title:'设置节',itemHeight:0.6786,headerHeight:0.819,relation:[1,0,0,0],cssUnit:'rem',itemShowCount:7,oneLevelId:oneLevelId,twoLevelId:twoLevelId,threeLevelId:threeLevelId,callback:function(selectOneObj,selectTwoObj,selectThreeObj){var s=0,e=0;for(var n=0;n<zperiodArr.length;n+=1){if(zperiodArr[n]==selectOneObj.value){s=n}if(zperiodArr[n]==selectThreeObj.value){e=n}}zperiodDom.find('span').text('第'+(s+1)*1+'—'+(e+1)*1+'节课');zperiodDom.find('span').attr({'data-start':selectOneObj.value,'data-zhi':selectTwoObj.value,'data-end':selectThreeObj.value})}})})};win['CXFun']=new CXFun()})(window);